import { Component } from '@angular/core';

@Component({
  selector: 'app-policy-new',
  template: `
    <div class="policy-new-container">
      <h1>New Insurance Policy</h1>
      <p>Create a new insurance policy for your vehicle.</p>

      <mat-card>
        <mat-card-content>
          <div class="form-section">
            <h2>Vehicle Information</h2>
            <div class="form-row">
              <mat-form-field appearance="outline">
                <mat-label>License Plate</mat-label>
                <input matInput placeholder="e.g. กก-1234 กรุงเทพมหานคร">
              </mat-form-field>

              <mat-form-field appearance="outline">
                <mat-label>Vehicle Make</mat-label>
                <mat-select>
                  <mat-option value="toyota">Toyota</mat-option>
                  <mat-option value="honda">Honda</mat-option>
                  <mat-option value="nissan">Nissan</mat-option>
                  <mat-option value="mazda">Mazda</mat-option>
                </mat-select>
              </mat-form-field>
            </div>
          </div>

          <div class="form-actions">
            <button mat-stroked-button routerLink="/policies">
              Back to Policies
            </button>
            <button mat-raised-button color="primary" type="button">
              Create Policy
            </button>
          </div>
        </mat-card-content>
      </mat-card>
    </div>
  `,
  styles: [`
    .policy-new-container {
      padding: 24px;
      max-width: 800px;
      margin: 0 auto;
    }

    .form-section h2 {
      margin-bottom: 16px;
      color: #333;
    }

    .form-row {
      display: grid;
      grid-template-columns: 1fr 1fr;
      gap: 16px;
      margin-bottom: 16px;
    }

    .form-actions {
      display: flex;
      gap: 16px;
      justify-content: flex-end;
      margin-top: 24px;
    }

    @media (max-width: 768px) {
      .form-row {
        grid-template-columns: 1fr;
      }

      .form-actions {
        flex-direction: column;
      }
    }
  `]
})
export class PolicyNewComponent {
}
